import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../context/UserContext';
import { Link } from 'react-router-dom';

const LoginForm = () => {
    const [Username, setUsername] = useState('');
    const [Password, setPassword] = useState('');
    const { setUser } = useContext(UserContext);
    const navigate = useNavigate();

    const handleLogin = async (e) => {
      e.preventDefault();
      try {
          const response = await axios.post('https://localhost:7115/api/auth/login', { Username, Password });
          setUser(response.data); // Assuming the response contains user data
          navigate('/calendar');
      } catch (error) {
          console.error('Login failed:', error);
        }
    };

    return (
      <div className="login-container">
          <div className="login-header">
              <h1>Welcome Our GYM</h1>
              <div className="login-tabs">
                  <Link to="/login" className="active">Sign In</Link>
                  <Link to="/register">Sign Up</Link>
              </div>
          </div>
          <form onSubmit={handleLogin} className="login-form">
              <div className="form-group">
                  <input
                      type="text"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                      placeholder="Username or email"
                      required
                  />
              </div>
              <div className="form-group">
                  <input
                      type="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      placeholder="Password"
                      required
                  />
                  <span className="toggle-password">
                      <i className="fa fa-eye"></i>
                  </span>
              </div>
              <button type="submit" className="login-btn">Sign In</button>
          </form>
      </div>
  );
};

export default LoginForm;
